export const getTagsData = (tagsIds) => {
  if (!tagsIds) {
    return allTagsData
  }

  let allTagsIds = Object.keys(allTagsData)
  let searchedTagsIds = allTagsIds.filter((tagId) => tagsIds.includes(tagId))

  let result = {}

  searchedTagsIds.forEach((tagId) => {
    result[tagId] = allTagsData[tagId]
  })

  return result
}

